        .init = NULL, .destroy = NULL, .clone = NULL,
        .update = NULL, .fixedUpdate = NULL, .render = NULL,
        .onEnabled = NULL, .onDisabled = NULL, .onGameObjectDestroyed = NULL,
        .getSerializedSize = NULL, .serialize = NULL, .deserialize = NULL
    };

