    printf("After fast iteration helpers test\n");

    // test_null_pointer_safety();
    // printf("After null pointer safety test\n");

    printf("GameObject tests completed with %d failures\n", failures);
    return failures;
}

#ifdef TEST_STANDALONE
int main(void) {
    printf("Starting GameObject tests...\n");
    fflush(stdout);
    int result = run_game_object_tests();
    printf("GameObject tests finished with result: %d\n", result);
    return result;
}
#endif

