    printf("\n");

    benchmark_object_updates();
    printf("\n");

    benchmark_large_scale_collision_detection();
    printf("\n");

    benchmark_memory_usage();
    printf("\n");

    printf(" All spatial grid performance tests passed!\n");
    return 0;
}
#endif

